import React, { Component } from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import Hasil from './Hasil'
import './App.css';

export default class Test extends Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      data: [],
      perPage: 10,
      currentPage: 0
    };
    this.handlePageClick = this.handlePageClick.bind(this);
  }
  receivedData() {
    axios
      .get(
        `https://storage.scrapinghub.com/items/427748?apikey=470eb897efea4b36976d4989cc77c7e0&format=json`
      )
      .then(res => {
        const data = res.data;
        // console.log(res.data);
        const slice = data.slice(
          this.state.offset,
          this.state.offset + this.state.perPage
        );
        const postData = slice.map(pd => (
          <React.Fragment>
          <div className="container">
              <div className="row">
              <div className="col-md-4 top">
                    <div className="card">
                        <img className="card-img-top shop-item-image" src={pd.image} alt="Card cap"/>
                        <div className="card-body">
                          <h5 className="card-title shop-item-title">{pd.title}</h5>
                          <span className="shop-item-price">{pd.price}</span>
                          {/* <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p> */}
                          <button className="btn btn-primary shop-item-button" type="button">ADD TO CHART</button>
                        </div>
                    </div>
                </div>
              </div>
          </div>
          </React.Fragment>
        ));

        this.setState({
          pageCount: Math.ceil(data.length / this.state.perPage),
          postData
        });
      });
  }
  handlePageClick = e => {
    const selectedPage = e.selected;
    const offset = selectedPage * this.state.perPage;

    this.setState(
      {
        currentPage: selectedPage,
        offset: offset
      },
      () => {
        this.receivedData();
      }
    );
  };

  componentDidMount() {
    this.receivedData();
  }
  render() {
    return (
      <div>
        <Hasil datas = {this.state.data} />
        {/* {this.state.postData} */}
        <ReactPaginate
          previousLabel={'prev'}
          nextLabel={'next'}
          breakLabel={'...'}
          breakClassName={'break-me'}
          pageCount={this.state.pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={this.handlePageClick}
          containerClassName={'pagination'}
          subContainerClassName={'pages pagination'}
          activeClassName={'active'}
        />
      </div>
    );
  }
}
