import React, { Components } from 'react';
import axios from 'axios';

export default class DataScrap extends Components {
    state = {
        datas: [],
        errors: null
    }

    getDatas () {
        axios
        .get("http://localhost:3000/crawl.json?start_requests=true&spider_name=babastudio")
        .then(response => 
            response.data.items.map(
                data => ({
                    title: `${data.title}`,
                    price: `${data.price}`,
                    image: `${data.image}`
                })
            )
        )
        .then(datas => {
            this.setState({
                datas
            },
            console.log(datas)
            )
        })
        .catch(error => this.setState({ error}));
    }

    componentDidMount() {
        this.getDatas();
    }

    render(){
        const { datas } = this.state

        return (
            <React.Fragment>
                <h1>Data</h1>
                {datas.map(data => {
                    const {title, price, image} = data;

                    return (
                        <div>
                            <img src={image} alt='images' />
                            <h1>{ title }</h1>
                            <p>{ price }</p>
                        </div>
                    )
                })}
            </React.Fragment>
        )
    }

}

// export default DataScrap;
