import React from 'react';
// import axios from 'axios';

class Datascrap extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      datas: [],
      errors: null
    };
  }

  componentDidMount() {
    fetch(
      'http://localhost:4000/crawl.json?start_requests=true&spider_name=babastudio'
    )
      .then(response => response.json())
      .then(resData => {
        //console.log(JSON.stringify(resData))
        //do your logic here
        //let datas = resData.items
        this.setState({ datas: resData.items }); //this is an asynchronous function
      });
  }

  render() {
    return (
      <div>
        {this.state.datas.map((data, index) => {
          return (
            <div className="container">
              <div className="row">
                {/* <div className="col-md-4">
                  <div key={index}>
                    <img src={data.image} alt='gbr' />
                    <h3>{data.title}</h3>
                    <p>{data.price}</p>
                  </div>
                </div> */}
                <div className="col-md-4 top">
                    <div className="card" style="width: 18rem;">
                        <img className="card-img-top shop-item-image" src={data.image} alt="Card image cap"/>
                        <div className="card-body">
                            <h5 className="card-title shop-item-title">{data.title}</h5>
                            <span className="shop-item-price">{data.price}</span>
                            {/* <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p> */}
                            <button className="btn btn-primary shop-item-button" type="button">ADD TO CHART</button>
                        </div>
                    </div>
                </div>
              </div>
            </div>

          );
        })}
      </div>
    );
  }
}

export default Datascrap;
